    def body =  """
        ${html_header}
        ${css}
        <div class="block2">
            ${jenkins_logo}
        </div>
        
        <div>
            <strong>artifact:</strong> ${pom.groupId}.${pom.artifactId}<br/>
            <strong>version:</strong> version ${pom.version}<br/>
            <strong>status:</strong> <span class="${buildStatus}">${buildStatus}</span><br/>
            <strong>changes:</strong><br/>
            <ul>
                ${changes.collect {
                    e -> "<li>"+e+"</li>"
                }.join("")}
            </ul>
        </div>
        ${html_footer}
    """

    sendMail(mailList,subject,body,buildStatus)
}

def sendMail(String[] mailList, subject, body, buildStatus){

    for (int i = 0; i < mailList.length; i++) {
        echo(mailList[i])
        emailext(to: mailList[i],
                subject: subject,
                body: body,
                from: "${env.JENKINS_EMAIL}",
                mimeType: "text/html",
                attachLog: buildStatus == 'FAILURE')
    }
}